import React from 'react';
import request from 'sync-request';
import * as d3 from 'd3';
import {Line} from 'react-chartjs-2';


var buttonStyle = {
    margin: '10px 10px 10px 0'
};


var state = {
    labels:[],
    datasets: [],
}


class DispContainer extends React.Component{




    constructor(props){
        super();





    }
    getTangleData(){
        var res = request('GET','http://localhost:5000/getReportData');
        var workData = res.body;
        this.state.datasets.append({
            label: workData[''],



        })



        return 0;
    }

    render(){

        return(
            <div>
                <row style={{
                    display:'flex',
                    justifyContent:"center",
                    alignItems:'center'
                }}>
                    <button
                        className="btn btn-default"
                        style={buttonStyle}
                        onClick={this.getTangleData}>Save Data</button>
                    <button
                    className="btn btn-default"
                    style={buttonStyle}
                    onClick={this.getTangleData}>Shutdown and Save Plots</button>

                </row>

            </div>
        );


    }
}

export default DispContainer;
